import Accordion from '@ux/accordion';
import '@ux/accordion/styles';
import Collapsible from '@ux/collapsible';
import '@ux/collapsible/styles';
import Checkmark from '@ux/icon/checkmark';
import '@ux/icon/checkmark/styles';
import lipsum from 'bob-ross-lipsum';
import Flag from '@ux/icon/flag';
import '@ux/icon/flag/styles';
import React from 'react';

export default function Indicator() {
  return (
    <Accordion title='An Accordion with Indicators'>
      <Collapsible title='Success' indicator={{ icon: Checkmark, background: 'bg-success' }}>
        <p>{ lipsum(10) }</p>
      </Collapsible>

      <Collapsible title='Warning' indicator={{ icon: Flag, background: 'bg-warning' }}>
        <p>{ lipsum(10) }</p>
      </Collapsible>

      <Collapsible title='No indicator'>
        <p>{ lipsum(10) }</p>
      </Collapsible>
    </Accordion>
  );
}
