import Accordion from '@ux/accordion';
import '@ux/accordion/styles';
import Collapsible from '@ux/collapsible';
import '@ux/collapsible/styles';
import lipsum from 'bob-ross-lipsum';
import React, { useState } from 'react';

export default function Open() {
  const [open, setOpen] = useState(2);

  return (
    <Accordion title='An Accordion Example' open={ open }>
      <Collapsible title={ lipsum(1) }>
        <p>{lipsum(10)}</p>
        <button onClick={ () => { setOpen(2); } }>Change to 2</button>
      </Collapsible>

      <Collapsible title={ lipsum(1) }>
        <p>{lipsum(10)}</p>
        <button onClick={ () => { setOpen(3); } }>Change to 3</button>
      </Collapsible>

      <Collapsible title={ lipsum(1) }>
        <p>{lipsum(10)}</p>
        <button onClick={ () => { setOpen(1); } }>Change to 1</button>
      </Collapsible>
    </Accordion>
  );
}
