import Intents, { enums } from '@ux/intents';

export default (function () {
  const intents = new Intents();

  intents.create('@ux/accordion', {
    styleProperties: [
      enums.properties.BORDER_COLOR
    ]
  });

  intents.uxAccordion.borderColor = intents.ux.box.borderColor;

  return intents;
}());
