/* eslint-disable no-alert */
import React from 'react';
import Alert from '../src';
import Button from '@ux/button';
import text from '@ux/text';

export default function Action() {
  const [show, setShow] = React.useState(true);
  const title = <text.h3 as='title' text='You should do something' />;

  return (
    <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
      <Alert
        title={ title }
        id='example-1'
        actions={ <Button text='Do something' design='inline' as='cta'/> }
        emphasis='critical'>
        Message suggesting that you do something
      </Alert>

      <Alert
        title={ title }
        id='example-2'
        onClose={ () => setShow(false) }
        actions={ <Button text='Do something' design='primary' as='cta'/> }
        emphasis='warning'>
        Message suggesting that you do something
      </Alert>

      { show && <Alert
        title={ title }
        id='example-3'
        actions={ <Button text='Do something' design='secondary' as='cta'/> }
        emphasis='success'>
          Message suggesting that you do something and is dismissible
      </Alert> }
    </div>
  );
}
/* eslint-enable no-alert */
