import React from 'react';
import Alert from '../src';
import text from '@ux/text';

export default function Links() {
  const messageWithLink = <span>
    you might want to <a href='#example-alert-link' className='alert-link'>click here</a> to do something.
  </span>;
  const title = <text.h3 as='title' text='You should do something' />;

  return <div className='spaced-group'>
    <Alert
      title={ title }
      id='success'
      emphasis='success'>
      { messageWithLink }
    </Alert>

    <Alert
      title={ title }
      id='warning'
      emphasis='warning'>
      { messageWithLink }
    </Alert>

    <Alert
      title={ title }
      id='critical'
      emphasis='critical'>
      { messageWithLink }
    </Alert>

    <Alert
      title={ title }
      id='info'
      emphasis='info'>
      { messageWithLink }
    </Alert>

    <Alert
      title={ title }
      id='highlight'
      emphasis='highlight'>
      { messageWithLink }
    </Alert>

    <Alert
      title={ title }
      id='neutral'
      emphasis='neutral'>
      { messageWithLink }
    </Alert>

    <Alert
      title={ title }
      id='internal'
      emphasis='internal'>
      { messageWithLink }
    </Alert>
  </div>;
}
