import Button from '../src';
import React, { useState } from 'react';
import Star from '@ux/icon/star';

export default function GhostButton() {
  const [active, setActive] = useState(false);
  const manageState = {
    as: 'toggle',
    active,
    onClick: () => setActive(!active)
  };
  return (
    <div className='spaced-group'>
      <Button text='Default Button'/>

      <Button text='Default Button Disabled' disabled={ true }/>

      <Button text='Default Button as select' as='select' className='test-with-select'/>

      <Button text='Default Button as cta' as='cta' className='test-with-cta'/>

      <Button text='Default Button as external' as='external' className='test-with-external'/>

      <Button text='Default Button as toggle' { ...manageState } />

      <Button text='Default Button as link' href='#'/>

      <Button
        icon={ <Star/> }
        className='test-with-icon-only'
        title='Favorite this'
        aria-label='Favorite this'/>

      <Button text='Default Button with icon' icon={ <Star/> } className='test-with-icon'/>

      <Button
        icon={ <Star/> }
        as='select'
        className='test-with-select-icon'
        title='Select star type'
        aria-label='Select star type'/>

      <Button text='Small Default Button' size='small'/>

      <Button text='Small Default Button' size='small' icon={ <Star/> } />
    </div>
  );
}
