import Button from '../src';
import React, { useState } from 'react';
import Star from '@ux/icon/star';

export default function Primary() {
  const [active, setActive] = useState(false);
  const manageState = {
    as: 'toggle',
    active,
    onClick: () => setActive(!active)
  };
  return (
    <>
      <Button design='primary' text='Primary Button'/>
      <Button design='primary' text='Primary Button Disabled' disabled={ true }/>

      <Button design='primary' text='Primary Button as select' as='select' className='test-with-select'/>

      <Button design='primary' text='Primary Button as cta' as='cta' className='test-with-cta'/>

      <Button design='primary' text='Primary Button as external' as='external' className='test-with-external'/>

      <Button design='primary' text='Primary Button as toggle' as='toggle' { ...manageState } />

      <Button design='primary' text='Primary Button as link' href='#'/>

      <Button
        design='primary'
        icon={ <Star/> }
        className='test-with-icon-only'
        title='Favorite this'
        aria-label='Favorite this'/>

      <Button design='primary' text='Primary Button with icon' icon={ <Star/> } className='test-with-icon'/>

      <Button
        design='primary'
        icon={ <Star/> }
        as='select'
        className='test-with-select-icon'
        title='Select star type'
        aria-label='Select star type'/>

      <Button design='primary' text='Small Primary Button' size='small'/>
    </>
  );
}
