import Button from '../src';
import React, { useState } from 'react';
import Star from '@ux/icon/star';

export default function SecondaryButton() {
  const [active, setActive] = useState(false);
  const manageState = {
    as: 'toggle',
    active,
    onClick: () => setActive(!active)
  };
  return (
    <div className='spaced-group'>
      <Button design='secondary' text='Secondary Button'/>

      <Button design='secondary' text='Secondary Button Disabled' disabled={ true }/>

      <Button design='secondary' text='Secondary Button as select' as='select' className='test-with-select'/>

      <Button design='secondary' text='Secondary Button as cta' as='cta' className='test-with-cta'/>

      <Button design='secondary' text='Secondary Button as external' as='external' className='test-with-external'/>

      <Button design='secondary' text='Secondary Button as toggle' { ...manageState } />

      <Button design='secondary' text='Secondary Button as link' href='#'/>

      <Button
        design='secondary'
        icon={ <Star/> }
        className='test-with-icon-only'
        title='Favorite this'
        aria-label='Favorite this'/>

      <Button design='secondary' text='Secondary Button with icon' icon={ <Star/> } className='test-with-icon'/>

      <Button
        design='secondary'
        icon={ <Star/> }
        as='select'
        className='test-with-select-icon'
        title='Select star type'
        aria-label='Select star type'/>

      <Button design='secondary' text='Small Secondary Button' size='small'/>
    </div>
  );
}
