import Intents, { enums } from '@ux/intents';

export default (function () {
  const intents = new Intents();

  intents.create('@ux/collapsible', {
    styleProperties: [
      enums.properties.BACKGROUND_COLOR,
      enums.properties.FOREGROUND_COLOR,
      enums.properties.BORDER_COLOR
    ],
    features: {
      [enums.features.TITLE]: {
        styleProperties: [
          enums.properties.FONT_FAMILY,
          enums.properties.FONT_SIZE,
          enums.properties.FONT_WEIGHT,
          enums.properties.LINE_HEIGHT,
          enums.properties.FOREGROUND_COLOR
        ]
      },
      [enums.features.CONTENT]: {
        styleProperties: [
          enums.properties.BACKGROUND_COLOR,
          enums.properties.FOREGROUND_COLOR
        ]
      }
    }
  });

  intents.uxCollapsible.foregroundColor = intents.ux.box.foregroundColor;
  intents.uxCollapsible.backgroundColor = intents.ux.box.backgroundColor;
  intents.uxCollapsible.borderColor = intents.ux.box.borderColor;

  intents.uxCollapsible.title.fontFamily = intents.ux.textTitle.fontFamily;
  intents.uxCollapsible.title.fontSize = intents.ux.textTitle.fontSize;
  intents.uxCollapsible.title.fontWeight = intents.ux.textTitle.fontWeight;
  intents.uxCollapsible.title.lineHeight = intents.ux.textTitle.lineHeight;
  intents.uxCollapsible.title.foregroundColor = intents.ux.box.foregroundColor;

  intents.uxCollapsible.content.foregroundColor = intents.ux.boxLowContrast.foregroundColor;
  intents.uxCollapsible.content.backgroundColor = intents.ux.boxLowContrast.backgroundColor;

  return intents;
}());
