import Dialog from '@ux/dialog';
import text from '@ux/text';
import Button from '@ux/button';
import React from 'react';

export default function Dismiss() {
  const [show, setShow] = React.useState(true);

  return (
    <>
      { show && <Dialog
        id='default-example'
        title={ <text.h2 text='Red pill or blue pill?' size={ 2 } /> }
        actions={ <>
          <Button text='Red pill' design='critical'/>
          <Button text='Blue pill' design='primary'/>
        </> }
        onClose={ () => setShow(false) }>
        <p>You take the blue pill—the story ends,
        you wake up in your bed and believe whatever you want to believe.</p>
        <p>You take the red pill—you stay in Wonderland,
        and I show you how deep the rabbit hole goes.</p>
      </Dialog> }
    </>
  );
}
