import Dialog from '@ux/dialog';
import text from '@ux/text';
import Button from '@ux/button';
import React from 'react';

export default function Full() {

  const title = <text.h2 text='Red pill or blue pill? This decision is totally up to you; choose wisely' size={ 2 } />;
  const actions = (
    <>
      <Button text='Red pill' design='critical'/>
      <Button text='Blue pill' design='primary'/>
    </>
  );
  const onClose = () => console.log('close!');
  const content = (
    <ul style={{ margin: 0, padding: 0, listStyle: 'none' }}>
      <li>You take the blue pill—the story ends,
      you wake up in your bed and believe whatever you want to believe.</li>
      <li>You take the red pill—you stay in Wonderland,
      and I show you how deep the rabbit hole goes.</li>
    </ul>
  );

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', gap: '3rem' }}>

      <Dialog
        emphasis='critical'
        title={ title }
        actions={ actions }
        onClose={ onClose }>
        { content }
      </Dialog>

      <Dialog
        emphasis='success'
        title={ title }
        actions={ actions }>
        { content }
      </Dialog>

      <Dialog emphasis='warning' title={ title }>
        { content }
      </Dialog>

      <Dialog title={ title } actions={ actions }>
        { content }
      </Dialog>

      <Dialog title={ title } actions={ actions } onClose={ onClose }>
        { content }
      </Dialog>

      <Dialog emphasis='warning'>
        { content }
      </Dialog>

      <Dialog emphasis='warning' onClose={ onClose }>
        { content }
      </Dialog>

      <Dialog emphasis='success' actions={ actions }>
        { content }
      </Dialog>

      <Dialog emphasis='info' actions={ actions } onClose={ onClose }>
        { content }
      </Dialog>
    </div>
  );
}
