import Disrupt from '../src';
import React from 'react';

import Datepicker from '@ux/datepicker';
import Dropdown from '@ux/dropdown';

import '@ux/datepicker/styles';
import '@ux/dropdown/styles';

const { DropdownItem } = Dropdown;

// eslint-disable-next-line react/prop-types
function Content({ title, id }) {
  return (
    <form className='content' style={{ minHeight: '50vh' }} id={ id }>
      <h2>{ title }</h2>
      <div>
        <label htmlFor={ `username-${id}` }>Username</label>
        <input id={ `username-${id}` } type='text'/>
      </div>
      <div>
        <label htmlFor={ `password-${id}` }>Password</label>
        <input id={ `password-${id}` } type='password'/>
      </div>

      <div>
        <label htmlFor={ `startDate-${id}` }>Start Date</label>
        <Datepicker
          id={ `startDate-${id}` }
          startDate={ new Date('February 14, 2015') }
          locale='en' />
      </div>

      <div>
        <label htmlFor='fruit'>Choose Fruit</label>
        <Dropdown type='select' name='myDropdown' id={ `fruit-${id}` }>
          <DropdownItem>Bananas.</DropdownItem>
          <DropdownItem><i>Apple</i></DropdownItem>
          <DropdownItem>Peach</DropdownItem>
          <DropdownItem>Orange</DropdownItem>
        </Dropdown>
      </div>

    </form>
  );
}

export default function Form() {
  const [show, setShow] = React.useState(false);

  return (
    <>
      <Content title='Page form' id='page'/>
      <button onClick={ () => setShow(!show) } id='show-overlay'>Toggle</button>
      { show && <Disrupt onClose={ () => setShow(false) } focusLock>
        <Content title='Overlay form' id='overlay'/>
      </Disrupt> }
    </>
  );
}
