/* eslint-disable no-console */
import Disrupt from '../src';
import React from 'react';

export default function Scroll() {
  const [count, setCount] = React.useState(0);
  return (
    <div style={{ display: 'flex', justifyContent: 'center' }}>
      <aside style={{ transform: 'scale(2)' }}>
        { Array(10).fill('The quick brown fox jumps over the lazy dog. \n').map((msg, i) => <p key={ i }>{msg}</p>) }
      </aside>
      <Disrupt portalElem={ document.body } onClose={ () => setCount(count + 1) }>
        <section className='content'>
          <p>Tried to escape <span className='count'>{ count }</span> times.</p>
          { Array(100).fill('The quick brown fox jumps over the lazy dog. \n').map((msg, i) => <p key={ i }>{msg}</p>) }
        </section>
      </Disrupt>
    </div>
  );
}
