import Intents from '@ux/intents';

// eslint-disable-next-line max-statements
export default (function () {
  const intents = new Intents();

  intents.create('@ux/disrupt', {
    styleProperties: [
      'backdropBlur',
      'backdropSaturate'
    ]
  });

  intents.uxDisrupt.backdropBlur = 0;
  intents.uxDisrupt.backdropSaturate = 1;

  return intents;

}());
