import React, { useState, useCallback } from 'react';
import Dropdown from '@ux/dropdown';
import '@ux/dropdown/styles';

const { DropdownDivider, DropdownItem } = Dropdown;

export default function External() {
  const defaultSelected = [0];
  const [selected, setSelected] = useState({
    name: 'first',
    index: defaultSelected
  });

  const onChange = useCallback((data) => {
    setSelected({
      name: data.children,
      index: data.selected
    });
  });

  return (
    <div>
      <Dropdown type='select' onChange={ onChange } defaultSelected={ defaultSelected }>
        <DropdownItem>Banana</DropdownItem>
        <DropdownItem>Apple</DropdownItem>
        <DropdownDivider />
        <DropdownItem>Peach</DropdownItem>
        <DropdownItem>Orange</DropdownItem>
        <DropdownDivider />
        <DropdownItem>Lettuce</DropdownItem>
        <DropdownItem>Spinach</DropdownItem>
        <DropdownDivider />
        <DropdownItem>Carrot</DropdownItem>
        <DropdownItem>Broccoli</DropdownItem>
      </Dropdown>

      <p>{`selected: ${selected.name} on index ${selected.index}`}</p>
    </div >
  );
}
