import React from 'react';
import root from 'react-shadow';
import Dropdown from '@ux/dropdown';
import '@ux/dropdown/styles';

const { DropdownItem } = Dropdown;

function styles() {
  return Array.from(document.styleSheets).map(styleSheet => {
    try {
      return Array.from(styleSheet.cssRules).map(rule => rule.cssText).join('');
    } catch (e) {
      console.error(e);
    }
  }).filter(Boolean).join('');
}

export default function ShadowDom() {
  return (
    <root.div className='shadow-host'>
      <style type='text/css'>{styles()}</style>
      <Dropdown type='select' name='myDropdown'>
        <DropdownItem>Bananas are some seriously tasty treats. I really can&#39;t say enough about them.</DropdownItem>
        <DropdownItem><i>Apple</i></DropdownItem>
        <DropdownItem>Peach</DropdownItem>
        <DropdownItem>Orange</DropdownItem>
      </Dropdown>
    </root.div>
  );
}
