import Intents, { enums } from '@ux/intents';

// eslint-disable-next-line max-statements
export default (function () {
  const intents = new Intents();

  intents.create('@ux/field-frame', {
    styleProperties: [
      enums.properties.BORDER_COLOR,
      enums.properties.BORDER_RADIUS,
      enums.properties.BACKGORUND_COLOR,
      enums.properties.BORDER_WIDTH
    ],
    expressions: [
      enums.expressions.CRITICAL
    ]
  });

  intents.uxFieldFrame.backgroundColor = intents.ux.control.backgroundColor;
  intents.uxFieldFrame.borderColor = intents.ux.control.borderColor;
  intents.uxFieldFrame.borderWidth = intents.ux.control.borderWidth;
  intents.uxFieldFrame.borderRadius = intents.ux.control.borderRadius;

  intents.uxFieldFrameCritical.borderColor = intents.ux.feedbackHighContrastCritical.feedbackColor;

  return intents;

}());
