import Fieldset from '@ux/fieldset';
import '@ux/fieldset/styles';
import React, { useRef, useEffect } from 'react';

export default function FieldsetExampleRef() {
  const _fieldRef = useRef();

  useEffect(() => {
    const fieldsetText = _fieldRef.current.props.children;
    const newElement = document.createElement('span');
    newElement.innerText = `I got the Fieldset's text: ${fieldsetText}`;
    document.getElementById('fieldset-example').appendChild(newElement);
  }, []);

  return <div className='spaced-group' id='fieldset-example'>
    <Fieldset ref={ _fieldRef }>
          Hello World.
    </Fieldset>
  </div>;
}
