import Flyout from '../src';
import React from 'react';

export default function Arrow(args) {
  const [show, setShow] = React.useState(false);
  const anchorRef = React.useRef(null);

  const flyoutTrigger = (
    <button
      aria-labelledby='with-arrow'
      ref={ anchorRef }
      onClick={ () => setShow(!show) }>
      Click to toggle flyout
    </button>
  );

  const flyoutContent = (
    <p>You can add an arrow to flyout.</p>
  );

  return (
    <div className='scroll-container'>
      <div className='canvas-container'>
        { flyoutTrigger }
        { show && <Flyout anchorRef={ anchorRef } arrow={ true } className='content' { ...args }>
          { flyoutContent }
        </Flyout> }
      </div>
    </div>
  );
}

Arrow.args = {
  id: 'with-arrow',
  role: 'tooltip',
  placement: 'top'
};
