import Flyout from '../src';
import React from 'react';

export default function Default(args) {
  const [show, setShow] = React.useState(false);
  const anchorRef = React.useRef(null);

  const flyoutTrigger = (
    <button
      aria-labelledby='with-default'
      ref={ anchorRef }
      onClick={ () => setShow(!show) }>
      Click to toggle flyout
    </button>
  );

  const flyoutContent = (
    <p>Here is some flyout content</p>
  );

  return (
    <div className='scroll-container'>
      <div className='canvas-container'>
        { flyoutTrigger }
        { show && <Flyout anchorRef={ anchorRef } className='content' { ...args }>
          { flyoutContent }
        </Flyout> }
      </div>
    </div>
  );
}

Default.args = {
  id: 'with-default'
};
