import Flyout from '../src';
import React from 'react';

export default function Placement(args) {
  const [show, setShow] = React.useState(false);
  const anchorRef = React.useRef(null);

  const flyoutTrigger = (
    <button
      aria-labelledby='with-placement'
      ref={ anchorRef }
      onClick={ () => setShow(!show) }>
      Click to toggle flyout
    </button>
  );

  const flyoutContent = (
    <>
      <p>placement=&#39;top&#39; is set here.</p>
      <p>But it&#39;ll still flip if it doesn&#39;t fit.</p>
    </>
  );

  return (
    <div className='scroll-container'>
      <div className='canvas-container'>
        { flyoutTrigger }
        { show && <Flyout
          anchorRef={ anchorRef }
          arrow={ true }
          onPlacement={ (placement) => console.log(placement) }
          className='content'
          { ...args }>
          { flyoutContent }
        </Flyout> }
      </div>
    </div>
  );
}

Placement.args = {
  placement: 'top',
  id: 'with-placement',
  role: 'tooltip'
};
