import { Checkbox, Form } from '@ux/uxcore2';
import React from 'react';

export default class CheckboxError extends React.Component {
  constructor() {
    super(...arguments);

    this.state = {
      ...this.state,
      isChecked: true
    };
  }

  onCheckboxChange() {
    this.setState({ isChecked: !this.state.isChecked });
  }

  render() {
    let errorMessage = '';

    if (!this.state.isChecked) {
      errorMessage = 'Errors upon errors!';
    }

    return (
      <Form>
        <Checkbox
          label='Errors abound...maybe, try unchecking me' name='foo' value='bar'
          onChange={ this.onCheckboxChange.bind(this) }
          checked={ this.state.isChecked }
          validates={ this.state.isChecked }
          failure={ errorMessage }/>
      </Form>
    );
  }
}
