import { Button, Checkbox, Form } from '@ux/uxcore2';
import React from 'react';

export default class CheckboxInteractive extends React.Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }

  render() {
    const button = (
      <Button
        design='primary'
        text={ this.state.count }
        onClick={ () => this.setState({ count: this.state.count + 1 }) }/>
    );
    const labelContent = (
      <p>
        Click this button to change its count { button }
      </p>
    );

    return (
      <Form>
        <Checkbox label={ labelContent } value='foo' name='bar' id='interactive' />
      </Form>
    );
  }
}
