import { FormElement, Form } from '@ux/uxcore2';
import React from 'react';

export default function ClearableControlled() {
  const [value, setValue] = React.useState();

  function validate(value) {
    return value === 'boo';
  }

  return (
    <Form action='#'>
      <FormElement
        label='Clearable input element, type boo to validate'
        name='clearable'
        value={ value }
        size='large'
        clearable
        onChange={ e => setValue(e.target.value) }
        validate={ validate }
      />
    </Form>
  );
}
