import { Password, Criteria } from '@ux/uxcore2';
import React from 'react';

const {
  MinCharactersRule,
  IncludeNumberRule,
  IncludeUppercaseRule,
  IncludeLowercaseRule,
  NoOuterWhitespaceRule
} = Criteria.Rules;

export default class CriteriaConnectedExample extends React.Component {
  render() {
    return (
      <Criteria.Connect>
        <Password label='Password' name='examplePassword' placeholder='password'/>

        <Criteria>
          <Criteria.Header>Your password must</Criteria.Header>

          <MinCharactersRule min={ 9 }>
            Be at least 9 characters
          </MinCharactersRule>

          <IncludeUppercaseRule>
            Include an uppercase letter
          </IncludeUppercaseRule>

          <IncludeLowercaseRule>
            Include a lowercase letter
          </IncludeLowercaseRule>

          <IncludeNumberRule>
            Include a number
          </IncludeNumberRule>

          <NoOuterWhitespaceRule>
            It would not, should not contain a space. It would not, should not be the case.
            If it should not have it here or there. It should not have it anywhere.
          </NoOuterWhitespaceRule>
        </Criteria>
      </Criteria.Connect>
    );
  }
}
