import { IntlProvider, FormattedMessage } from 'react-intl';
import { FormElement, Criteria } from '@ux/uxcore2';
import React from 'react';

const { NoOuterWhitespaceRule } = Criteria.Rules;

const messages = {
  cc: 'Your country code should',
  whitespace: 'Not start or end with a space'
};

export default class CriteriaInternationalExample extends React.Component {
  render() {
    return (
      <IntlProvider locale='en' messages={ messages }>
        <Criteria.Connect>
          <FormElement type='text' label='Language' maxLength={ 4 } placeholder='Enter your country code' />
          <Criteria>
            <Criteria.Header>
              <FormattedMessage id='cc' />
            </Criteria.Header>

            <NoOuterWhitespaceRule>
              <FormattedMessage id='whitespace' />
            </NoOuterWhitespaceRule>
          </Criteria>
        </Criteria.Connect>
      </IntlProvider>
    );
  }
}
