import { FormElement, Form, Tooltip } from '@ux/uxcore2';
import React from 'react';

export default class Custom extends React.Component {
  render() {
    const label = (
      <span id='custom-label-id' style={{ color: 'teal', fontWeight: 'bold' }}>
        <Tooltip title='Title' message='You can put whatever you want in a label.'>Labels can be more than text!</Tooltip>
      </span>
    );

    const label2 = <span>
      This label does not have an ID prop so we have to specify one on the input
    </span>;

    return (
      <Form action='#'>
        <FormElement label={ label } name='example' />
        <FormElement label={ label2 } id='custom-label-2' name='example2' />
      </Form>
    );
  }
}
