import { QuantitySelector } from '@ux/uxcore2';
import React from 'react';

export default class QuantityOnchange extends React.Component {
  constructor(...args) {
    super(...args);

    this.state = {
      ...this.state,
      value: '',
      isValid: null
    };

    this.onChange = this.onChange.bind(this);
  }

  onChange(value, isValid) {
    this.setState({ value, isValid });
  }

  render() {
    return (
      <div>
        <span>Last <code>onChange</code> value received</span>
        <pre><code>
          { JSON.stringify(this.state || {}) }
        </code></pre>

        <QuantitySelector
          id='onchange-quantity-selector'
          label='Select a value between 1 and 100'
          onChange={ this.onChange }
          errorMessage='Your input is invalid.'
          defaultValue={ 10 }
          max={ 100 }
          min={ 1 } />
      </div>
    );
  }
}
