import { Search } from '@ux/uxcore2';
import React from 'react';

export default class SearchTruncateResults extends React.Component {
  render() {
    const data = [];
    const randWords = [
      'copper', 'explain', 'truck', 'neat', 'unite', 'branch', 'educated', 'tenuous', 'hum', 'decisive', 'neatness'
    ];

    for (let i = 0; i < 1000; i += 1) {
      data.push(randWords[i % randWords.length] + ' ' + i);
    }

    return (
      <Search
        data={ data }
        maxResults={ 10 }
        hideTruncatedResults
        placeholder='Type "neatness 1". Results should be hidden until results less than 10'
        id='hideTruncate'
      />
    );
  }
}
