import { Search } from '@ux/uxcore2';
import React from 'react';

export default class SearchLimitedResults extends React.Component {
  render() {
    const data = [];
    const randWords = [
      'copper', 'explain', 'truck', 'neat', 'unite', 'branch', 'educated', 'tenuous', 'hum', 'decisive', 'neatness'
    ];

    for (let i = 0; i < 1000; i += 1) {
      data.push(randWords[i % randWords.length] + ' ' + i);
    }

    return (
      <Search
        data={ data }
        maxResults={ 10 }
        placeholder='Type "neatness". Results should be truncated to 10 items'
        id='truncate'
      />
    );
  }
}
