import { FormElement, Form } from '@ux/uxcore2';
import React from 'react';

export default class Size extends React.Component {
  render() {
    return (
      <Form action='#'>
        <FormElement label='Small' name='sm' size='small' />
        <FormElement label='Large' name='lg' size='large' />
        <FormElement label='Default' name='default' />
      </Form>
    );
  }
}
