import { FormElement, Form } from '@ux/uxcore2';
import React from 'react';

export default class Validation extends React.Component {
  render() {
    /**
     * An example action for when the button is clicked.
     *
     * @param {String} value The value of the input element.
     * @returns {Boolean} indication if the value is valid.
     * @private
     */
    function validate(value) {
      return /^\d+$/.test(value);
    }

    return (
      <Form action='#'>
        <FormElement label='Numbers only' name='example' validate={ validate } />
        <FormElement label='Numbers only with failure message'
          name='example-msg'
          validate={ validate }
          failure='Input must only include numbers' />
        <FormElement label='Invalid input state' name='example-invalid' validates={ false } />
        <FormElement label='Valid input state' name='example-valid' validates={ true } />
      </Form>
    );
  }
}
