import React from 'react';
import PropTypes from 'prop-types';
import Rule from '../criteria-rule';

/**
 * Rule component requiring an uppercase character.
 *
 * @displayName IncludeUppercaseRule
 *
 * @param {Object} props Props
 * @returns {ReactElement} ReactElement
 */
export default function IncludeUppercaseRule(props) {
  return (
    <Rule validate={ (v) => /[A-Z]/.test(v) } { ...props }>
      { props.children }
    </Rule>
  );
}

/**
 * PropType validation.
 *
 * @type {Object}
 * @private
 */
IncludeUppercaseRule.propTypes = {
  /** Child elements */
  children: PropTypes.node
};
