import React from 'react';
import PropTypes from 'prop-types';
import Rule from '../criteria-rule';
import { detach } from '../util';

/**
 * Rule component requiring a minimum length of characters.
 *
 * @displayName MinCharactersRule
 *
 * @param {Object} props Props
 * @returns {ReactElement} ReactElement
 */
export default function MinCharactersRule(props) {
  var [a, b] = detach(props, 'min');
  return (
    <Rule validate={ (v) => v.length >= a.min } { ...b }>
      { props.children }
    </Rule>
  );
}

/**
 * PropType validation.
 *
 * @type {Object}
 * @private
 */
MinCharactersRule.propTypes = {
  /** The minimum number of characters (inclusive) */
  min: PropTypes.number.isRequired,
  /** Child elements */
  children: PropTypes.node
};
