import React from 'react';
import PropTypes from 'prop-types';
import Rule from '../criteria-rule';

/**
 * Rule component requiring no spaces at start or end of input.
 *
 * @displayName NoOuterWhitespaceRule
 *
 * @param {Object} props Props
 * @returns {ReactElement} ReactElement
 */
export default function NoOuterWhitespaceRule(props) {
  return (
    <Rule validate={ (v) => v.trim() === v } { ...props }>
      { props.children }
    </Rule>
  );
}

/**
 * PropType validation.
 *
 * @type {Object}
 * @private
 */
NoOuterWhitespaceRule.propTypes = {
  /** Child elements */
  children: PropTypes.node
};
