import FormElement from '../lib/index.js';

/**
 * Interface for creating radio elements. Note: this code is also used by React Native to set the type
 * of the Form Element component.
 *
 * @displayName Radio
 * @class Radio
 * @public
 */
export class Radio extends FormElement {
  constructor(...args) {
    super(...args);

    this.type = 'radio';
    this.isFlip = true;
  }

  render() {
    return super.render();
  }
}

export default Radio;
