import Growl from '../src';
import Button from '@ux/button';
import React from 'react';

export default function Default() {
  const [count, setCount] = React.useState(0);
  const config = {
    title: `Rawr!! ${count}`,
    content: 'I have a scary message',
    fadeTime: 2000,
    id: `rawr-${count}`
  };

  return (
    <>
      <Growl />
      <Button
        aria-haspopup={ true }
        onClick={ () => { Growl.addGrowlMessage(config); setCount(count + 1); } }
        id='persist-growl-button'
        text='Add message'/>
    </>
  );
}
