import Growl from '../src';
import Button from '@ux/button';
import React from 'react';

export default function Persist() {
  const [count, setCount] = React.useState(0);
  const config = {
    title: `Rawr!! ${count}`,
    content: 'I will persist!',
    icon: 'success',
    id: `rawr-${count}`,
    actions: <Button href='#' text='Go do this!' design='inline' as='cta'/>
  };

  return (
    <>
      <Growl />
      <Button
        aria-haspopup={ true }
        onClick={ () => { Growl.addGrowlMessage(config); setCount(count + 1); } }
        id='persist-growl-button'
        text='Add message'/>
    </>
  );
}
