/* global cy */
import { mount } from '../../../configs/cypress-config/mount';
import React from 'react';

import CloudUpload from '../cloud-upload';
import '../cloud-upload/index.css';

describe('@ux/icon', function () {
  it('renders an icon', function () {
    mount(<CloudUpload />);
    cy.get('@root').snapshot('default');
  });

  it('renders an icon with another theme', function () {
    mount(<CloudUpload />, { brand: 'go-dark:brand' });
    cy.get('@root').snapshot('dark');
  });

  it('should render a red 18 font icon', function () {
    mount(<CloudUpload width={ 18 } height={ 18 } fill='red' />);
    cy.get('svg use[*|href$="cloud-upload"]').should('have.attr', 'fill');
    cy.get('@root').snapshot('fill');
  });
});
