import Icon from '../index.js';
import icons from '../generated.json';
import { shallow } from 'enzyme';
import assume from 'assume';
import React from 'react';

describe('Icon:web', function () {
  Object.keys(icons).forEach(function each(name) {
    it(`renders the ${name} icon`, function () {
      const enzyme = shallow(<Icon name={ name } />);
      const output = enzyme.html();

      assume(output).includes('<span');
      assume(output).includes('</span>');
      assume(output).includes('uxicon-' + name);
    });
  });
});
