import Label from '@ux/label';
import '@ux/label/styles';
import React from 'react';

export default function Floating() {
  const [float, setFloat] = React.useState(false);

  return (
    <fieldset style={{ position: 'relative', paddingBottom: '.25rem' }} className='ux-text ux-text-input'>
      <Label internal={ true } float={ float } htmlFor='username'>Username</Label>
      <input
        id='username'
        style={{ marginTop: '.75rem', border: 0 }}
        onFocus={ () => setFloat(true) }
        onBlur={ () => setFloat(false) } />
    </fieldset>
  );
}
