import React, { useState } from 'react';
import Listbox from '../src';

const ITEMS = [
  {
    id: 'add',
    children: 'Add Item'
  },
  {
    id: 'edit',
    children: 'Edit User'
  },
  {
    id: 'delete',
    children: 'Delete a thing'
  }
];

export default function Controlled(args) {
  const [value, setValue] = useState('edit');
  return (
    <Listbox items={ ITEMS } selected={ value } onConfirm={ setValue } { ...args }/>
  );
}

Controlled.args = {
  stretch: true
};
