import React from 'react';
import Listbox from '../src';

const scrollableItems = [
  { id: 'add', children: 'Add Item' },
  { id: 'edit', children: 'Edit User' },
  { id: 'delete', children: 'Delete a thing' },
  { id: 'duplicate', children: 'Duplicate a thing' },
  { id: 'replace', children: 'Replace a thing' },
  { id: 'combine', children: 'Combine some things' },
  { id: 'get', children: 'Get a thing' },
  { id: 'copy', children: 'Copy a thing' },
  { id: 'paste', children: 'Paste a thing' },
  { id: 'draw', children: 'Draw a thing' },
  { id: 'sell', children: 'Sell a thing' },
  { id: 'buy', children: 'Buy a thing' }
];

export default function Scrollable(args) {
  return (
    <Listbox items={ scrollableItems } { ...args } />
  );
}

Scrollable.args = {
  stretch: true
};
