import { css } from 'linaria';
import Intents from '@ux/intents';

import '@ux/button/src/styles';
import '@ux/sibling-set/src/styles';

const intents = new Intents();

export default css`
:global() {
  .ux-listbox {
    background-color: ${intents.ux.box.backgroundColor};
    color: ${intents.ux.box.foregroundColor};
    border-color: ${intents.ux.box.borderColor};
    border-radius: ${intents.ux.box.borderRadius};

    & > [role] {
      max-height: max(50vh, calc(24 * ${intents.ux.box.density}));
      overflow: auto;
    }

    .ux-button {
      &[data-visually-focused] {
        color: ${intents.ux.actionHovered.foregroundColor};
        background: ${intents.ux.actionHovered.backgroundColor};
        border-color: ${intents.ux.actionHovered.borderColor};
      }
    }
  }
}`;
