import Modal from '../src';
import text from '@ux/text';
import Button from '@ux/button';
import React from 'react';

export default function ButtonExample() {
  const [show, setShow] = React.useState(false);
  const title = <text.h2 text='Never gonna give you up!' as='title'/>;
  const actions = (
    <>
      <Button design='primary' onClick={ () => setShow(false) } text='Continue'/>
      <Button design='secondary' onClick={ () => setShow(false) } text='Cancel'/>
    </>
  );
  const modal = (
    <Modal id='default-example' title={ title } onClose={ () => setShow(false) } actions={ actions }>
      <img
        style={{ maxWidth: '100%' }}
        src='https://media.giphy.com/media/6b9QApjUesyOs/giphy.gif'
        alt='Rick Astley singing Never Gonna Give You Up' />
      <p>
        Never gonna give you up<br />
        Never gonna let you down<br />
        Never gonna run around and desert you<br />
        Never gonna make you cry<br />
        Never gonna say goodbye<br />
        Never gonna tell a lie and hurt you
      </p>
    </Modal>
  );

  return (
    <>
      <Button aria-haspopup='true' design='primary' onClick={ () => { setShow(true); } } text='Open Modal with Buttons'/>
      { show && modal }
    </>
  );
}
