import Modal from '../src';
import text from '@ux/text';
import Button from '@ux/button';
import lipsum from 'bob-ross-lipsum';
import React from 'react';

export default function LongExample() {
  const [show, setShow] = React.useState(false);
  // eslint-disable-next-line max-len
  const title = <text.h2 text='Title of the modal which is really long as well so it flows to multiple paragraphs which should push the content down instead of flowing over the contents of the modal it self' as='title'/>;
  const modal = (
    <Modal id='long-example' title={ title } onClose={ () => setShow(false) }>
      { Array(100).join('1').split('1').map(function (data, index) {
        //
        // This is a really cheap way to just fill the modal dialog with
        // enough content to cause it to overflow and make the body/modal
        // scrollable
        //
        return (
          <p key={ index }>
            { lipsum(10) }
          </p>
        );
      }) }
    </Modal>
  );

  return (
    <>
      <Button aria-haspopup='true' design='primary' onClick={ () => { setShow(true); } } text='Open Long Modal'/>
      { show && modal }
    </>
  );
}
