import Modal from '../src';
import text from '@ux/text';
import Button from '@ux/button';
import React from 'react';

export default function TrappedExample() {
  const [show, setShow] = React.useState(false);
  const title = <text.h2 text='Title of the modal' as='title'/>;
  const modal = (
    <Modal id='trapped-example' title={ title } focusLock={ false }>
      Sorry, you are now trapped here
    </Modal>
  );

  return (
    <>
      <Button aria-haspopup='true' design='primary' onClick={ () => { setShow(true); } } text='Open Modal Forever'/>
      { show && modal }
    </>
  );
}
