import React from 'react';
import Pagination from '../src';

export default function Bookends(args) {
  // eslint-disable-next-line no-console
  const onChange = (pageSelected, pageItems) => console.log(`Page selected: ${pageSelected}, Page Items: ${pageItems}`);

  return (
    <div>
      <h3>With Bookends</h3>
      <Pagination
        onChange={ onChange }
        id='with-bookends'
        { ...args }
      />
      <hr/>
      <h3>Without bookends</h3>
      <Pagination
        onChange={ onChange }
        showBookends={ false }
        id='without-bookends'
        { ...args }
      />
    </div>
  );
}

Bookends.args = {
  totalItems: 100,
  pageItems: 20,
  pageItemsChoices: [5, 10, 20]
};
