/* eslint-disable jsx-a11y/no-onchange */
import React, { useState } from 'react';
import Pagination from '../src';

export default function Controlled(args) {
  const [defaultSelected, setDefaultSelected] = useState(1);
  const [pageItems, setPageItems] = useState(10);
  const [totalItems, setTotalItems] = useState(50);

  return (
    <div>
      <select onChange={ ({ target }) => setDefaultSelected(target.value) }>
        <option disabled selected value='default selected'>default selected</option>
        <option value={ 1 }>1</option>
        <option value={ 2 }>2</option>
        <option value={ 3 }>3</option>
      </select>
      <select onChange={ ({ target }) => setPageItems(target.value) }>
        <option disabled selected value='page items'>page items</option>
        <option value={ 10 }>10</option>
        <option value={ 20 }>20</option>
      </select>
      <select onChange={ ({ target }) => setTotalItems(target.value) }>
        <option disabled selected value='total items'>total items</option>
        <option value={ 50 }>50</option>
        <option value={ 100 }>100</option>
      </select>
      <hr/>
      <Pagination
        defaultSelected={ defaultSelected }
        pageItems={ pageItems }
        totalItems={ totalItems }
        { ...args }
      />
    </div>
  );
}
``;
