import React from 'react';
import Pagination from '../src';

export default function Dropdown(args) {
  // eslint-disable-next-line no-console
  const onChange = (pageSelected, pageItems) => console.log(`Page selected: ${pageSelected}, Page Items: ${pageItems}`);

  return (
    <div>
      <h3>Without Dropdown</h3>
      <Pagination
        onChange={ onChange }
        id='without-dropdown'
        { ...args }
      />
      <hr/>
      <h3>With Dropdown</h3>
      <Pagination
        onChange={ onChange }
        pageItemsChoices={ [5, 10, 20] }
        id='with-dropdown'
        { ...args }
      />
    </div>
  );
}

Dropdown.args = {
  totalItems: 100,
  pageItems: 20
};
